import { useState, useEffect } from 'react'
import ResultElem from "../components/resultelem"
function TabellineLog() {

    const [results, setResults]=useState([])
    const [currentquestion, setCurrentquestion]=useState([7,4])
    const [currentanswer, setCurrentanswer]=useState("")

    const handleSubmit=(event)=>{ 

        setCurrentquestion([Math.floor(Math.random()*10),Math.floor(Math.random()*10)])
        event.preventDefault()
        addResults()
    } 
    const addResults=()=>{
        let a=currentquestion[0] 
        let b=currentquestion[1] 

        if ((a*b)===currentanswer) {
            setResults((prevState)=>([...prevState,`${a}x${b}=${a*b} right`]))
        }
        else {
            setResults((prevState)=>([...prevState,`${a}x${b}=${a*b} wrong`]))
        }

    }
useEffect (()=>{
    //const testres=currentquestion[0]*currentquestion[1]
    //((testres===currentanswer)? setResults("RIGHT") : setResults("WRONG")

    },[currentanswer])

    const handleChange=(event)=>{
        setCurrentanswer(parseInt(event.target.value,10))
    }
    const displayLog = ()=>results.map((r)=>(<ResultElem text={r}/>))

    return (<div>
                <h1>TABELLINE: {currentquestion[0]}X{currentquestion[1]}</h1>
                <form onSubmit={handleSubmit}>
                    <input name="currentanswer" type="text" onChange={handleChange}/>
                    <button>ok</button>
                 </form>
                <div className="tabelline-log">
                {displayLog()}
                </div>
            </div>)
}


export default TabellineLog
